import{_ as e,o,c as r,d}from"./app-cc3b17e7.js";const t={},c=d('<h2 id="contentprovider-概述" tabindex="-1"><a class="header-anchor" href="#contentprovider-概述" aria-hidden="true">#</a> ContentProvider 概述</h2><h3 id="contentprovider" tabindex="-1"><a class="header-anchor" href="#contentprovider" aria-hidden="true">#</a> ContentProvider</h3><ul><li><code>ContentProvider</code> （内容提供者）是 <code>Android</code> 系统四大组件之一，它是不同应用程序之间进行数据共享的标准 <code>API</code> ，通过 <code>ContentResolver</code> 类可以访问 <code>ContentProvider</code> 中共享的数据</li><li><code>ContentProvider</code> 使用基于数据库模型的简单表格来提供需要共享的数据，在该表格中，每一行表示一条记录，而每一列代表特定类型和含义的数据，并且其中每一条数据记录都包含一个名为 <code>_ID</code> 的字段类标识每条数据</li></ul><h3 id="contentresolver" tabindex="-1"><a class="header-anchor" href="#contentresolver" aria-hidden="true">#</a> ContentResolver</h3><ul><li><code>ContentResolver</code> 提供一系列增删改查的方法对数据进行操作，并且这些方法以 <code>Uri</code> 的形式对外提供数据</li><li><code>Uri</code> 为内容提供者中的数据建立了唯一标识符。它主要由三部分组成，<code>scheme</code> 、<code>authorities</code> （主机名/域名）和 <code>path</code></li></ul><h2 id="创建-contentprovider" tabindex="-1"><a class="header-anchor" href="#创建-contentprovider" aria-hidden="true">#</a> 创建 ContentProvider</h2><h2 id="访问其他应用程序" tabindex="-1"><a class="header-anchor" href="#访问其他应用程序" aria-hidden="true">#</a> 访问其他应用程序</h2><h2 id="内容观察者" tabindex="-1"><a class="header-anchor" href="#内容观察者" aria-hidden="true">#</a> 内容观察者</h2>',8),n=[c];function a(i,h){return o(),r("div",null,n)}const l=e(t,[["render",a],["__file","B07chapter7.html.vue"]]);export{l as default};
